<script>
import Experts from '@/components/Experts'
import Clubs from '@/components/Clubs'
import Rings from '@/components/Rings'
import Ref from '@/components/Ref'



export default {
  name: 'App',
  components: {
    Experts, Clubs, Rings, Ref
  },
  data () {
    return {
      correct_login: "admin",
      correct_password: "admin",
      login: null,
      password: null, 
      tab:[ 'primary', 'secondary', 'secondary','secondary']
    }
  },
  methods: {
    change_tab: function (index) {
      let tab = ['secondary', 'secondary', 'secondary', 'secondary']
      tab[index] = 'primary'
      this.tab = tab
    },
    refreshAdd: function () {
      this.change_tab(6);
      setTimeout(() => {
        this.change_tab(5);
      });
    }, 
    refreshRemove: function () {
      this.change_tab(5);
      setTimeout(() => {
        this.change_tab(6);
      });
    } 
  }
}
